mapper.select:
SELECT s.id signalId, s.create_date createDate, s.modify_date modifyDate, s.user_id userId, s.signal_name signalName, s.signal_level signalLevel, s.signal_desc signalDesc, s.check_date checkDate, s.signal_tem signalTem, s.signal_lots signalLots, s.signal_lots_profit signalLotsProfit, s.signal_currency signalCurrency, s.signal_follows signalFollows, s.monthly_average_income monthlyAverageIncome, s.history_withdraw historyWithdraw, s.email, s.server_name serverName, s.mt_acc_id mtAccId, s.minimum, s.annualized_expected_return annualizedExpectedReturn, s.historical_return historicalReturn, s.suggest_cycle suggestCycle, s.remarks, s.proj_name projName, s.proj_key projKey, s.oper_user_id operUserId, v.valuation_date valuationDate, v.LEVEL, v.steady_score steadyScore, v.profit_score profitScore, v.risk_control_score riskControlScore, v.fund_size_score fundSizeScore, v.non_fluke_profit_score nonFlukeProfitScore, v.score, u.username, u.real_name realName, u.ref_name refName, u.introducer, u.recommend, u.sex, u.follow_level followLevel, u.avatar_url avatarUrl, m.broker_name brokerName, m.balance balance, m.leverage leverage, m.credit, m.profit, m.equity, m.margin FROM fu_product_signal s LEFT JOIN fu_product_signal_valuation v ON s.id = v.signal_id LEFT JOIN fu_user u ON s.user_id = u.id LEFT JOIN fu_account_mt m ON s.user_id=m.user_id and s.mt_acc_id=m.mt_acc_id where 1=1
and s.user_id = #{userId,jdbcType=INTEGER}
and s.id = #{signalId,jdbcType=INTEGER}
and s.mt_acc_id = #{mtAccId,jdbcType=INTEGER}
and m.broker_name = #{brokerName,jdbcType=INTEGER}
and v.LEVEL = #{level,jdbcType=INTEGER}

SELECT DISTINCT s.id, s.create_date, s.modify_date, s.user_id, s.signal_name, s.signal_level, s.signal_desc, s.signal_ratings, s.signal_state, s.apply_date, s.check_date, s.signal_tem, s.signal_lots, s.signal_lots_profit, s.signal_account_money, s.signal_currency, s.signal_follows, s.monthly_average_income, s.history_income, s.history_withdraw, s.email, s.phone, s.qq_number, s.server_name, s.mt_acc_id, s.mt_password_trade, s.mt_password_watch, s.minimum, s.annualized_expected_return, s.historical_return, s.suggest_cycle, s.remarks, s.connect_state, s.proj_key, s.proj_name, s.oper_user_id FROM fu_product_signal s LEFT JOIN fu_product_signal_permit p on s.id=p.signal_id where 1=1
and s.user_id = #{userId,jdbcType=INTEGER}
and s.id = #{signalId,jdbcType=INTEGER}
and s.mt_acc_id = #{mtAccId,jdbcType=INTEGER}
and p.proj_key = #{projKey,jdbcType=INTEGER}
