mapper.select:
SELECT f.user_id userId, f.signal_id signalId, f.user_server_name userServerName, f.user_mt_acc_id userMtAccId, f.signal_server_name signalServerName, f.signal_mt_acc_id signalMtAccId, f.create_date createDate, f.modify_date modifyDate, f.follow_state followState, f.follow_direct followDirect, f.follow_mode followMode, f.follow_type followType, f.follow_amount amount, u.username, u.real_name realName, u.ref_name refName, u.introducer, u.recommend, u.sex, u.follow_level followLevel, u.avatar_url avatarUrl, m.leverage, m.balance, m.credit, m.profit, m.equity, m.margin FROM fu_user_follows f LEFT JOIN fu_user u ON f.user_id = u.id LEFT JOIN fu_product_signal s ON f.signal_id = s.id LEFT JOIN fu_account_mt m ON f.user_id = m.user_id AND f.user_mt_acc_id = m.mt_acc_id where 1=1
and f.user_id = #{userId,jdbcType=INTEGER}
and f.signal_id = #{signalId,jdbcType=INTEGER}
and f.follow_state = #{followState,jdbcType=INTEGER}

SELECT f.id, f.user_id, f.signal_id, f.user_server_name, f.user_mt_acc_id, f.signal_server_name, f.signal_mt_acc_id, f.follow_state, f.follow_direct, f.follow_mode, f.follow_type, f.follow_amount, f.follow_max_amount, f.follow_min_amount, f.follow_alarm_amount, f.follow_max_hands, f.follow_min_hands, f.follow_alarm_lose, f.follow_alarm_level, f.account_equity_percentage, f.account_equity_amount, f.modify_date, f.create_date FROM fu_user_follows f LEFT JOIN fu_permission_userproject p ON f.user_id = p.user_id WHERE 1=1
and p.proj_key = #{projKey,jdbcType=INTEGER}
and f.signal_id = #{signalId,jdbcType=INTEGER}
and f.follow_state = #{followState,jdbcType=INTEGER}
and f.user_id = #{userId,jdbcType=INTEGER}
and f.user_server_name = #{userServerName,jdbcType=VARCHAR}
and f.user_mt_acc_id = #{userMtAccId,jdbcType=VARCHAR}
and f.signal_server_name = #{signalServerName,jdbcType=VARCHAR}
and f.signal_mt_acc_id = #{signalMtAccId,jdbcType=VARCHAR}
and f.follow_direct = #{followDirect,jdbcType=TINYINT}
and f.follow_mode = #{followMode,jdbcType=TINYINT}
and f.follow_type = #{followType,jdbcType=TINYINT}
